import axios from "axios";
import React, { useEffect, useState } from "react";

const Home = () => {
  const [publications, setPublications] = useState([]);
  const [title, setTitle] = useState(""); 
  const [, setCuisineType] = useState(""); 

  // Récupération des recettes au chargement
  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = () => {
    axios
      .get("http://localhost:8080/recipes", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((response) => {
        console.log(response.data);
        setPublications(response.data);
      });
  };


  const handleCreateRecipe = (e) => {
    e.preventDefault(); // Pour éviter le rechargement de la page

    axios
      .post(
        "http://localhost:8080/recipes",
        { title, cuisineType }, // corps de la requête
        {
          headers: {
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
        }
      )
      .then((response) => {
        console.log("Recette créée :", response.data);
        // On vide les champs
        setTitle("");
        setCuisineType("");
        // Et on recharge la liste
        fetchRecipes();
      })
      .catch((error) => {
        console.error("Erreur lors de la création :", error);
      });
  };

  return (
    <div>
      <h1>Liste des recettes</h1>

      <form onSubmit={handleCreateRecipe} style={{ marginBottom: "2rem" }}>
        <input
          type="text"
          placeholder="Titre de la recette"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Type de cuisine"
          value={cuisineType}
          onChange={(e) => setCuisineType(e.target.value)}
        />
        <button type="submit">Créer une recette</button>
      </form>

      <ul>
        {publications.map((recipe) => (
          <li key={recipe.id}>
            <p>{recipe.title}</p>
            <p>{recipe.cuisineType}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
