import axios from "axios";
import React, { useEffect, useState } from "react";

const Home = () => {
  const [publications, setPublications] = useState([]);
  const [title, setTitle] = useState(""); 
  const [content, setContent] = useState(""); 

  // Récupération des recettes au chargement
  useEffect(() => {
    fetchPublications();
  }, []);

  const fetchPublications = () => {
    axios
      .get("http://localhost:8080/publications", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((response) => {
        console.log(response.data);
        setPublications(response.data);
      });
  };


  const handleCreateRecipe = (e) => {
    e.preventDefault(); // Pour éviter le rechargement de la page

    axios
      .post(
        "http://localhost:8080/publications",
        { title, cuisineType: content }, // corps de la requête
        {
          headers: {
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
        }
      )
      .then((response) => {
        console.log("Recette créée :", response.data);
        // On vide les champs
        setTitle("");
        setContent("");
        // Et on recharge la liste
        fetchPublications();
      })
      .catch((error) => {
        console.error("Erreur lors de la création :", error);
      });
  };

  return (
    <div>
      <h1>Liste des publications</h1>

      <form onSubmit={handleCreateRecipe} style={{ marginBottom: "2rem" }}>
        <input
          type="text"
          placeholder="Titre de la recette"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Type de cuisine"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <button type="submit">Créer une publication</button>
      </form>

      <ul>
        {publications.map((publication) => (
          <li key={publication.id}>
            <p>{publication.title}</p>
            <p>{publication.cuisineType}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
