import axios from "axios";
import React, { useEffect, useState } from "react";
import "./Home.css"; // Import du fichier CSS

const Home = () => {
  const [annonces, setAnnonces] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  useEffect(() => {
    fetchAnnonces();
  }, []);

  const fetchAnnonces = () => {
    axios
      .get("http://localhost:8080/annonces", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((response) => {
        setAnnonces(response.data);
      })
      .catch((error) => {
        console.error("Erreur lors du chargement des annonces :", error);
      });
  };

  const handleCreateRecipe = (e) => {
    e.preventDefault();

    axios
      .post(
        "http://localhost:8080/annonces",
        { title, content },
        {
          headers: {
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
        }
      )
      .then((response) => {
        setTitle("");
        setContent("");
        fetchAnnonces();
      })
      .catch((error) => {
        console.error("Erreur lors de la création :", error);
      });
  };

  return (
    <div className="container">
      <h1>Liste des annonces</h1>

      <form onSubmit={handleCreateRecipe} className="publication-form">
        <input
          type="text"
          placeholder="Titre de l'annonce"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Contenu..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="4"
        />
        <button type="submit">Créer une annonce</button>
      </form>

      <ul className="publication-list">
        {annonces.map((annonce) => (
          <li key={annonce.id} className="publication-item">
            <p>Auteur : {annonce.author.name}</p>
            <p>Titre : {annonce.title}</p>
            <p>{annonce.content}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
